<?php

function parseArray( $aArray )
{
  foreach( $aArray as &$v )
  {
    if( is_array($v) )
      $v = parseArray($v);
    if( is_bool($v) )
    {
      if( $v === true )
        $v = 'true';
      elseif( $v === false )
        $v = 'false';
    }
  }

  return $aArray;
}

function dump( $arg, $bReturn = false, $bNL2BR = false )
{
  ob_start();
  if( is_array($arg) )
  {
    if( $bReturn === true && $bNL2BR !== true )
      print_r(parseArray($arg));
    else
      echo '<pre>'.print_r(parseArray($arg),true).'</pre>';
  }
  elseif( is_string($arg) || is_numeric($arg) )
    echo $arg;
  elseif( is_null($arg) )
    echo 'NULL';
  else
    var_dump($arg);

  if( $bReturn === true )
    return ob_get_clean();
  else
    ob_end_flush();
}

# strip_urls() recycled from cleaner() on http://stackoverflow.com/questions/1113840/php-remove-url-from-string
function strip_urls( $s )
{
  $U = explode(' ', $s);

  $W =array();
  foreach ($U as $k => $u):
    if( stristr($u,".") )
    { # only preg_match if there is a dot    
      if( containsTLD($u) === true )
      {
        unset($U[$k]);
        return strip_urls( implode(' ',$U) );
      }      
    }
  endforeach;

  return implode(' ',$U);
}

